mapper.select:
SELECT * FROM (
	(SELECT ROWNUM AS RNO, t.* FROM
		(select
			
				
					from tbl_board
				
			
				
					
						from tbl_board where b_title like '%'|| #{WORD} ||'%'
					
					
						from tbl_board where b_content like '%'|| #{WORD} || '%'
					
					
						from tbl_board where b_writer like '%'|| #{WORD} || '%'
					
				
			order by b_no desc) t
		)
	) WHERE RNO BETWEEN #{START} AND #{END}
